/**
 * Result panel component
 * Displays analysis results including understood/misunderstood/missing concepts
 */

import React from 'react';

function ConceptList({ concepts, title, className }) {
  if (!concepts || concepts.length === 0) {
    return null;
  }

  return (
    <div className={`concept-section ${className}`}>
      <h3>{title}</h3>
      <ul>
        {concepts.map((concept, index) => (
          <li key={index}>
            <strong>{concept.name}</strong>
            <span className="confidence">
              (Confidence: {(concept.confidence * 100).toFixed(1)}%)
            </span>
            {concept.details && (
              <p className="concept-details">{concept.details}</p>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

function ResultPanel({ result }) {
  if (!result) {
    return null;
  }

  return (
    <div className="result-panel">
      <h2>Analysis Results</h2>
      
      <div className="scores-summary">
        <div className="score-item">
          <label>Overall Score:</label>
          <span>{(result.overall_score * 100).toFixed(1)}%</span>
        </div>
        <div className="score-item">
          <label>Coverage Score:</label>
          <span>{(result.coverage_score * 100).toFixed(1)}%</span>
        </div>
        <div className="score-item">
          <label>Correctness Score:</label>
          <span>{(result.correctness_score * 100).toFixed(1)}%</span>
        </div>
      </div>

      <ConceptList 
        concepts={result.concepts_understood}
        title="✅ Concepts You Understand"
        className="understood"
      />

      <ConceptList 
        concepts={result.concepts_misunderstood}
        title="❌ Concepts You Misunderstood"
        className="misunderstood"
      />

      <ConceptList 
        concepts={result.concepts_missing}
        title="❓ Missing Concepts"
        className="missing"
      />

      {result.feedback && (
        <div className="feedback-section">
          <h3>Feedback</h3>
          <p>{result.feedback}</p>
        </div>
      )}

      {result.suggestions && result.suggestions.length > 0 && (
        <div className="suggestions-section">
          <h3>Suggestions for Improvement</h3>
          <ul>
            {result.suggestions.map((suggestion, index) => (
              <li key={index}>{suggestion}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default ResultPanel;
