/**
 * Text input component
 * Handles user input for explanation text, subject, and topic selection
 */

import React, { useState } from 'react';

const SUBJECTS = [
  { value: 'data_structures', label: 'Data Structures' },
  { value: 'algorithms', label: 'Algorithms' },
  { value: 'operating_systems', label: 'Operating Systems' },
  { value: 'databases', label: 'Databases' },
  { value: 'computer_networks', label: 'Computer Networks' },
  { value: 'software_engineering', label: 'Software Engineering' }
];

function TextInput({ onAnalyze, isLoading }) {
  const [explanation, setExplanation] = useState('');
  const [subject, setSubject] = useState('');
  const [topic, setTopic] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!explanation.trim() || !subject || !topic.trim()) {
      alert('Please fill in all fields');
      return;
    }
    
    onAnalyze(explanation, subject, topic);
  };

  return (
    <form onSubmit={handleSubmit} className="text-input-form">
      <div className="form-group">
        <label htmlFor="subject">Subject:</label>
        <select 
          id="subject"
          value={subject} 
          onChange={(e) => setSubject(e.target.value)}
          required
        >
          <option value="">Select a subject</option>
          {SUBJECTS.map(subj => (
            <option key={subj.value} value={subj.value}>
              {subj.label}
            </option>
          ))}
        </select>
      </div>

      <div className="form-group">
        <label htmlFor="topic">Topic:</label>
        <input
          type="text"
          id="topic"
          value={topic}
          onChange={(e) => setTopic(e.target.value)}
          placeholder="e.g., Binary Search Trees"
          required
        />
      </div>

      <div className="form-group">
        <label htmlFor="explanation">Your Explanation:</label>
        <textarea
          id="explanation"
          value={explanation}
          onChange={(e) => setExplanation(e.target.value)}
          placeholder="Explain the concept in your own words..."
          rows={8}
          required
        />
      </div>

      <button 
        type="submit" 
        disabled={isLoading}
        className="analyze-button"
      >
        {isLoading ? 'Analyzing...' : 'Analyze My Explanation'}
      </button>
    </form>
  );
}

export default TextInput;
